import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import Header from '../Components/Header';
import Loading from './Loading';
import './CssPages/Profile.css';
import { getUser } from '../services/userAPI';

export default class Profile extends Component {
  constructor(props) {
    super(props);
    this.state = {
      profile: {},
      isLoading: false,
    };
  }

  componentDidMount() {
    this.handleGetUser();
  }

  handleGetUser = async () => {
    this.setState({ isLoading: true });
    this.setState({ profile: await getUser() });
    this.setState({ isLoading: false });
  }

  render() {
    const { profile: { name, email, image, description }, isLoading } = this.state;
    return (
      <div className="profile" data-testid="page-profile">
        <Header className="header-profile" />
        {isLoading ? <Loading />
          : (
            <section className="profile-content">
              <div>
                <img
                  className="image"
                  src={ image }
                  alt={ `Foto de perfil do ${name}` }
                />
                <Link to="/profile/edit">
                  <button
                    type="button"
                    className="buttom-profile"
                  >
                    Editar perfil
                  </button>

                </Link>
              </div>
              <div>
                <p>{name}</p>
                <p>{email}</p>
                <p>{description}</p>
              </div>
            </section>)}
      </div>
    );
  }
}
