import React, { Component } from 'react';
import './style.css';
import { Link } from 'react-router-dom';
import logoTunes from '../../images/logo-trybeTunes_white.png';
import { getUser } from '../../services/userAPI';

export default class Header extends Component {
  constructor(props) {
    super(props);
    this.state = {
      userName: 'Usuário',
      isLoading: false,
      foto: '',
    };
  }

  componentDidMount() {
    this.setName();
  }

  setName = async () => {
    this.setState({ isLoading: true });
    const userObject = await getUser();
    this.setState({ userName: userObject.name });
    this.setState({ foto: URL.createObjectURL(userObject.foto) });
    this.setState({ isLoading: false });
  }

  render() {
    const { userName, isLoading } = this.state;
    return (
      <div className="header-container">
        <header data-testid="header-component">
          <span>
            <Link to="/">
              <img src={ logoTunes } alt="Logo da TrybeTunes" />
            </Link>
          </span>
          <div>
            <span data-testid="header-user-name">
              {isLoading ? 'Carregando...' : userName}
            </span>
          </div>
        </header>
        <nav>
          <Link
            className="link1"
            data-testid="link-to-search"
            to="/search"
          >
            <h2>Pesquisa</h2>

          </Link>
          <Link
            className="link2"
            data-testid="link-to-favorites"
            to="/favorites"
          >
            <h2>Favoritas</h2>

          </Link>
          <Link
            className="link3"
            data-testid="link-to-profile"
            to="/profile"
          >
            <h2>Perfil</h2>

          </Link>
        </nav>
      </div>
    );
  }
}
