import React, { Component } from 'react';

export default class MusicCard extends Component {
  render() {
    const { trackInfo: {
      previewUrl,
      key,
      trackName,
      trackId,
    } } = this.props;

    return (
      <div key={ key }>
        <span>{trackName}</span>
        <audio data-testid="audio-component" src={ previewUrl } controls>
          <track kind="captions" />
        </audio>
        <input data-testid={ `checkbox-music-${trackId}` } type="checkbox">Favorita</input>
      </div>

    );
  }
}
