import React, { Component } from 'react';
import './CssPages/Album.css';
import Header from '../Components/Header';
import MusicCard from '../Components/MusicCard';
import getMusics from '../services/musicsAPI';
import { getFavoriteSongs } from '../services/favoriteSongsAPI';

export default class Album extends Component {
  constructor(props) {
    super(props);
    this.state = {
      trackMusics: [],
      isChecked: true,
    };
  }

  componentDidMount() {
    this.handleAlbumTracks();
  }

  handleAlbumTracks = async () => {
    const { match: { params } } = this.props;
    const trackMusics = await getMusics(params.id);
    this.setState({ trackMusics });
    // this.handleIsChecked();
  }

  handleIsChecked = async (trackId) => {
    const checkSaveds = await getFavoriteSongs();
    console.log(checkSaveds);
    this.setState({
      isChecked: checkSaveds
        .some((song) => song.trackId === trackId),
    });
  }

  render() {
    const { trackMusics, isChecked } = this.state;
    const albumInfo = { ...trackMusics[0] };
    return (
      <div className="album" data-testid="page-album">
        <Header className="header-album" />
        <section className="album-section">
          <img src={ albumInfo.artworkUrl100 } alt="Foto Capa do Album" />
          <h1 data-testid="artist-name">{ albumInfo.artistName }</h1>
          <h2 data-testid="album-name">{ albumInfo.collectionName }</h2>
          <div>
            {trackMusics
              .map((track, i) => i !== 0
                && <MusicCard
                  key={ track.trackId }
                  trackInfo={ track }
                  isChecked={ isChecked }
                  handleIsChecked={ this.handleIsChecked }
                />)}
          </div>
        </section>
      </div>
    );
  }
}
