import React, { Component } from 'react';
import PropTypes from 'prop-types';
import './CssPages/Login.css';
import Loading from './Loanding';
import logoTunes from '../images/logo-trybetunes_black.png';
import { createUser } from '../services/userAPI';

export default class Login extends Component {
  constructor(props) {
    super(props);
    this.state = {
      userInfo: {},
      isDisabled: true,
      isLoading: false,
      foto: {},
    };
  }

  handleFillForm = ({ target: { name, value, files } }) => {
    this.setState(({ userInfo }) => ({ userInfo: { ...userInfo, [name]: value } }),
      this.validationFillForm(name, value));
    if (name === 'image') {
      this.setState({ foto: files[0] });
      console.log(files[0]);
    }
  }

  validationFillForm = (name, value) => {
    this.setState({ isDisabled: name && (value.length <= 2) });
  }

  handleClickSubmitUser = async () => {
    this.setState({ isLoading: true });
    const { userInfo } = this.state;
    await createUser(userInfo);
    const { history } = this.props;
    history.push('/search');
  }

  render() {
    const { userInfo: { name }, isDisabled, isLoading, foto } = this.state;
    console.log(foto);

    return (isLoading ? <Loading className="login-loading" />
      : (
        <div className="login" data-testid="page-login">

          <span className="logoTunes">
            <img src={ logoTunes } alt="Logo da TrybeTunes" />
            {foto
              ? <img
                  alt={ `Foto do perfil de  ${name}` }
                  data-testid="login-image-input"
                  className="image-login"
                  onChange={ this.handleFillForm }
                  src={ (window.URL || window.webkitURL).createObjectURL(foto) }
                  width="10px"
              />
              : ''}
          </span>

          <form className="form">

            <input
              name="name"
              placeholder="Digite seu Nome"
              data-testid="login-name-input"
              type="text"
              className="name-login"
              onChange={ this.handleFillForm }
            />

            <input
              name="email"
              placeholder="Digite seu Email"
              data-testid="login-email-input"
              type="email"
              className="email-login"
              onChange={ this.handleFillForm }
            />

            <input name="image" type="file" onChange={ this.handleFillForm } />

            <textarea
              name="description"
              placeholder="Conte um pouco sobre seu perfil musical"
              data-testid="login-description-input"
              className="description-login"
              maxLength="300"
              onChange={ this.handleFillForm }
            />

            <button
              data-testid="login-submit-button"
              disabled={ isDisabled }
              className="button-login"
              type="button"
              onClick={ this.handleClickSubmitUser }
              tabIndex="0"
              onKeyDown={ this.handleClickSubmitUser }
            >
              Entrar
            </button>
          </form>

        </div>)
    );
  }
}

Login.propTypes = {
  history: PropTypes.arrayOf(PropTypes.string).isRequired,
};
