import React, { Component } from 'react';
import './style.css';
import { Link } from 'react-router-dom';
import logoTunes from '../../images/logo-trybeTunes_white.png';
import { getUser } from '../../services/userAPI';
import Loading from '../../Pages/Loanding';

export default class Header extends Component {
  constructor(props) {
    super(props);
    this.state = {
      userName: 'Usuário',
      isLoading: false,
    };
  }

  componentDidMount() {
    this.setName();
  }

  setName = async () => {
    this.setState({ isLoading: true });
    const userObject = await getUser();
    this.setState({ userName: userObject.name });
    this.setState({ isLoading: false });
  }

  render() {
    const { userName, isLoading } = this.state;
    return (isLoading ? <Loading className="header-loading" />
      : (
        <div className="header-container">
          <header data-testid="header-component">
            <span>
              <Link to="/">
                <img src={ logoTunes } alt="Logo da TrybeTunes" />
              </Link>
            </span>
            <div>
              <span data-testid="header-user-name">
                {isLoading ? 'Carregando...' : userName}
              </span>
            </div>
          </header>
          <nav>
            <h2 data-testid="link-to-search">
              <Link to="/search">Pesquisa</Link>
            </h2>
            <h2 data-testid="link-to-favorites">
              <Link to="/favorites">Favoritas</Link>
            </h2>
            <h2 data-testid="link-to-profile">
              <Link to="/profile">Perfil</Link>
            </h2>
          </nav>
        </div>)
    );
  }
}
