import React, { Component } from 'react';
import Header from '../Components/Header';
import Loading from './Loading';
import { getUser } from '../services/userAPI';

export default class ProfileEdit extends Component {
  constructor(props) {
    super(props);
    this.state = {
      userData: {},
      isLoading: false,
      isDisabled: false,
    };
  }

  componentDidMount() {
    this.setUserData();
  }

  handleClickSubmitUser = () => {

  }

  setUserData = async () => {
    this.setState({ userData: await getUser() });
  }

  handleFillForm= ({ target: { name, value } }) => {
    this.setState({ isLoading: true });

    this.setState(({ userData }) => ({ userData: { ...userData, [name]: { value } } }));

    this.setState({ isLoading: false });
  }

  render() {
    const { isLoading, isDisabled,
      userData: { name, email, image, description } } = this.state;
    return (
      <div data-testid="page-profile-edit">
        <Header />
        {isLoading ? <Loading className="edit-loading" />
          : (
            <section>
              <span className="logoTunes">

                {image
                  ? (
                    <img
                      alt={ `Foto do perfil de  ${name}` }
                      data-testid="edit-input-image"
                      className="image-edit"
                      onChange={ this.handleFillForm }
                      src={ image }
                      width="10px"
                    />)
                  : 'Sem foto'}
              </span>

              <form className="form">

                <input
                  name="name"
                  value={ name }
                  placeholder="Ajuste seu Nome"
                  data-testid="edit-input-name"
                  type="text"
                  className="name-edit"
                  onChange={ this.handleFillForm }
                />

                <input
                  name="email"
                  value={ email }
                  placeholder="Digite seu novo email"
                  data-testid="edit-input-email"
                  type="email"
                  className="email-edit"
                  onChange={ this.handleFillForm }
                />

                <input name="image" type="file" onChange={ this.handleFillForm } />

                <textarea
                  name="description"
                  value={ description }
                  placeholder="Ajuste seu perfil"
                  data-testid="edit-input-description"
                  className="description-edit"
                  maxLength="300"
                  onChange={ this.handleFillForm }
                />

                <button
                  data-testid="edit-button-save"
                  disabled={ isDisabled }
                  className="button-edit"
                  type="button"
                  onClick={ this.handleClickSubmitUser }
                >
                  Entrar
                </button>
              </form>
            </section>)}
      </div>
    );
  }
}
