import React, { Component } from 'react';
import { addSong } from '../../services/favoriteSongsAPI';
import Loading from '../../Pages/Loanding';

export default class MusicCard extends Component {
  constructor(props) {
    super(props);
    this.state = {
      isLoading: false,

    };
  }

  handleSaveFavorites = async () => {
    this.setState({ isLoading: true });
    const { trackInfo, trackId, handleIsChecked } = this.props;
    handleIsChecked(trackId);
    await addSong(trackInfo);
    this.setState({ isLoading: false });
  }

  render() {
    const { trackInfo: {
      previewUrl,
      key,
      trackName,
      trackId,
    },
    isChecked,
    } = this.props;
    const { isLoading } = this.state;

    return (
      <div key={ key }>
        <span>{trackName}</span>
        <audio data-testid="audio-component" src={ previewUrl } controls>
          <track kind="captions" />
        </audio>
        <label htmlFor="favorites" className="checkbox">
          {isLoading ? <Loading />
            : (
              <input
                name="favorites"
                data-testid={ `checkbox-music-${trackId}` }
                type="checkbox"
                checked={ isChecked }
                onChange={ this.handleSaveFavorites }
              />
            )}
          Favorita
        </label>
      </div>

    );
  }
}
