import React, { Component } from 'react';
import Header from '../Components/Header';
import './CssPages/Favorites.css';
import Loading from './Loading';
import { getFavoriteSongs, removeFavoriteSongs } from '../services/favoriteSongsAPI';

export default class Favorites extends Component {
  constructor(props) {
    super(props);
    this.state = {
      favoriteSongs: [],
      isLoading: false,
    };
  }

  componentDidMount() {
    this.handleGetFavorites();
  }

  handleGetFavorites = () => {
    this.setState({ isLoading: true });
    this.setState({ favoriteSongs: getFavoriteSongs() });
  }

  render() {
    const { favoriteSongs, isLoading } = this.state;
    return (
      <div className="favorites" data-testid="page-favorites">
        <Header className="header-favorites" />
        {isLoading ? <Loanding className="favorites-loading" />
          : (
            <section section className="favorites-content">
              {favoriteSongs.map((track) => (<MusicCard
                key={ track.trackId }
                trackInfo={ track }
              />))}
            </section>
          )}

      </div>
    );
  }
}
