import React, { Component } from 'react';
import { Switch, Route, Redirect } from 'react-router-dom';
import Login from '../../Pages/Login';
import Album from '../../Pages/Album';
import NotFound from '../../Pages/NotFound';
import Search from '../../Pages/Search';
import Favorites from '../../Pages/Favorites';
import Profile from '../../Pages/Profile';
import ProfileEdit from '../../Pages/ProfileEdit';
import Loading from '../../Pages/Loanding';
import { createUser } from '../../services/userAPI';
import './styles.css';

export default class Content extends Component {
  constructor(props) {
    super(props);
    this.state = {
      userForm: {
        name: '',
        email: '',
        image: '',
        description: '',
      },
      isDisabled: true,
      isLogged: false,
    };
  }

  handleFillForm = ({ target: { name, value } }) => {
    this.setState(({ userForm }) => ({ userForm: { ...userForm, [name]: value } }),
      this.validationForm(name, value));
  }

  validationForm = (name, value) => {
    if (name === 'name') {
      this.setState({ isDisabled: value.length <= 2 });
    }
  }

  handleLoginSubmit = async () => {
    const { userForm } = this.state;
    this.setState({ isLogged: await createUser(userForm) });
  }

  componentDidMount({ props: { handleGetUser } }) {
    this.props.handleGetUser();
  }

  render() {
    const { userForm: { name }, isDisabled, isLogged } = this.state;
    return (
      <main className="content-routes">
        <Switch>
          <Route
            exact
            path="/"
            render={ () => (
              <Login
                handleFillForm={ this.handleFillForm }
                handleLoginSubmit={ this.handleLoginSubmit }
                name={ name }
                isDisabled={ isDisabled }
              />
            ) }
          />

          <Route exact path="/search" component={ Search } />
          <Route exact path="/album/:id" component={ Album } />
          <Route exact path="/favorites" component={ Favorites } />
          <Route exact path="/profile" component={ Profile } />
          <Route exact path="/profile/edit" component={ ProfileEdit } />
          <Route
            exact
            path="/loading"
            render={ () => (
              isLogged === 'OK'
                ? <Redirect to="/search" />
                : <Loading />
            ) }
          />
          <Route path="*" component={ NotFound } />
        </Switch>
      </main>
    );
  }
}
