import React, { Component } from 'react';
import './CssPages/Login.css';
import logoTunes from '../images/logo-trybetunes_black.png';
import { createUser } from '../services/userAPI';

export default class Login extends Component {
  constructor(props) {
    super(props);
    this.state = {
      userInfo: {},
      isDisabled: true,
    };
  }

  handleFillForm = ({ target: { name, value } }) => {
    this.setState({ userInfo: { [name]: value } }, this.validationFillForm(name, value));
  }

  validationFillForm = (name, value) => {
    this.setState({ isDisabled: name && (value.length <= 2) });
  }

  handleUserLogSubmit = async () => {
    const { userInfo } = this.state;
    await createUser(userInfo);
    this.Redirect('/loading');
  }

  render() {
    const { userInfo: { name }, isDisabled } = this.state;
    return (
      <div className="login" data-testid="page-login">

        <span className="image-container">
          <img src={ logoTunes } alt="Logo da TrybeTunes" />
        </span>

        <form className="form">
          <label htmlFor="name">
            <input
              name="name"
              value={ name }
              data-testid="login-name-input"
              type="text"
              className="name-login"
              onChange={ this.handleFillForm }
            />
            nome
          </label>
          <label htmlFor="email">
            <input
              name="email"
              data-testid="login-email-input"
              type="email"
              className="email-login"
              onChange={ this.handleFillForm }
            />
            email
          </label>
          <label htmlFor="image">
            <input name="image" type="file" />
            <img
              alt={ `Foto do perfil de  ${name}` }
              data-testid="login-image-input"
              className="image-login"
              onChange={ this.handleFillForm }
            />
            Imagem
          </label>
          <label htmlFor="description">
            <textarea
              name="description"
              data-testid="login-description-input"
              className="description-login"
              onChange={ this.handleFillForm }
            />
            descrição
          </label>
          <button
            data-testid="login-submit-button"
            disabled={ isDisabled }
            className="button-login"
            type="button"
            onClick={ this.handleUserLogSubmit }
          >
            Entrar
          </button>
        </form>

      </div>
    );
  }
}
