import React, { Component } from 'react';
import Header from '../Components/Header';
import Loading from './Loading';
import './CssPages/ProfileEdit.css';
import { getUser, updateUser } from '../services/userAPI';

export default class ProfileEdit extends Component {
  constructor(props) {
    super(props);
    this.state = {
      userData: {},
      isLoading: true,
      isDisabled: false,
    };
  }

  componentDidMount() {
    this.setUserData();
  }

  setUserData = async () => {
    this.setState({ userData: await getUser() });
    this.setState({ isLoading: false });
  }

  handleFillForm = ({ target: { name, value, files } }) => {
    this.setState(({ userData }) => ({
      userData: {
        ...userData,
        [name]: name === 'image'
          ? URL.createObjectURL(files[0])
          : value,
      },
    }), this.validationFillForm(name, value));
  }

  validationFillForm = (name, value) => {
    this.setState({ isDisabled: name && value.length });
  }

  handleClickSubmitUser = async () => {
    const { userData } = this.state;
    await updateUser(userData);
    const { history } = this.props;
    history.push('/profile');
  }

  render() {
    const { isLoading, isDisabled,
      userData: { name, email, image, description } } = this.state;
    return (
      <div className="edit">
        <Header className="header-edit" />
        {isLoading ? <Loading className="edit-loading" />
          : (
            <div className="form-container" data-testid="page-profile-edit">

              <section className="form-section">
                <span className="logoTunes">

                  <img
                    alt={ `Foto do perfil de  ${name}` }
                    data-testid="edit-input-image"
                    className="image-edit"
                    onChange={ this.handleFillForm }
                    src={ image }
                    width="100px"
                  />
                  )

                </span>

                <form className="form">

                  <input
                    name="name"
                    value={ name }
                    placeholder="Ajuste seu Nome"
                    data-testid="edit-input-name"
                    type="text"
                    className="name-edit"
                    onChange={ this.handleFillForm }
                  />

                  <input
                    name="email"
                    value={ email }
                    placeholder="Digite seu novo email"
                    data-testid="edit-input-email"
                    type="email"
                    className="email-edit"
                    onChange={ this.handleFillForm }
                    required
                  />

                  <input name="image" type="file" onChange={ this.handleFillForm } />

                  <textarea
                    name="description"
                    value={ description }
                    placeholder="Ajuste seu perfil"
                    data-testid="edit-input-description"
                    className="description-edit"
                    maxLength="300"
                    onChange={ this.handleFillForm }
                  />

                  <button
                    data-testid="edit-button-save"
                    disabled={ isDisabled }
                    className="button-edit"
                    type="button"
                    onClick={ this.handleClickSubmitUser }
                  >
                    Editar perfil
                  </button>
                </form>
              </section>
            </div>)}
      </div>
    );
  }
}
