import React, { Component } from 'react';
import './CssPages/Login.css';
import { createUser } from '../services/userAPI';

export default class Login extends Component {
  constructor(props) {
    super(props);
    this.state = {
      name: '',
      email: '',
      image: '',
      description: '',
    };
  }

  handleName = ({ target: { name, value } }) => {
    this.setState({ [name]: value });
  }

  render() {
    const { name, handleName } = this.state;
    return (
      <div data-testid="page-login" className="login">

        <form className="form">
          <label htmlFor="name">

            <input
              name="name"
              data-testid="login-name-input"
              type="text"
              className="name-login"
              onChange={ handleName }
            />
          </label>
          <label htmlFor="email">

            <input
              name="email"
              data-testid="login-email-input"
              type="email"
              className="email-login"
              onChange={ handleName }
            />
          </label>
          <label htmlFor="image">

            <img
              name="image"
              alt={ `Foto do perfil de ${name}` }
              data-testid="login-image-input"
              className="image-login"
              onChange={ handleName }
            />
          </label>
          <label htmlFor="description">

            <textarea
              name="description"
              data-testid="login-description-input"
              className="description-login"
              onChange={ handleName }
            />
          </label>
          <button
            data-testid="login-submit-button"
            disabled
            className="button-login"
            type="button"
            onClick={ createUser(this.state) }
          >
            Entrar
          </button>
        </form>

      </div>
    );
  }
}
