import React, { Component } from 'react';
import PropTypes from 'prop-types';
import './CssPages/Login.css';
import { Link } from 'react-router-dom';
import Header from '../Components/Header';

export default class Login extends Component {
  constructor(props) {
    super(props);
    this.state = {
      userInfo: {},
      isLogged: false,
      isDisabled: true,
    };
  }

  handleFillForm = ({ target: { name, value } }) => {
    this.setState({ userInfo: { [name]: value } }, this.validationFillForm(name, value));
  }

  validationFillForm = (name, value) => {
    this.setState({ isDisabled: name && value <= 2 });
  }

  handleUserLog = async () => {
    this.setState({ isLogged: await createUser(userForm) });
    this.setState({ userInfo: await getUser() });
    console.log('App');
  }

  render() {
    const { isDisabled } = this.state;
    return (
      <div className="login" data-testid="page-login">
        <form className="form">

          <label htmlFor="name">
            <input
              name="name"
              data-testid="login-name-input"
              type="text"
              className="name-login"
              onChange={ this.handleFillForm }
            />
            nome
          </label>

          <label htmlFor="email">
            <input
              name="email"
              data-testid="login-email-input"
              type="email"
              className="email-login"
              onChange={ this.handleFillForm }
            />
            email
          </label>

          <label htmlFor="image">
            <input name="image" type="file" />
            <img
              alt={ `Foto do perfil de ${name}` }
              data-testid="login-image-input"
              className="image-login"
              onChange={ this.handleFillForm }
            />
            Imagem
          </label>

          <label htmlFor="description">
            <textarea
              name="description"
              data-testid="login-description-input"
              className="description-login"
              onChange={ this.handleFillForm }
            />
            descrição
          </label>

          <Link to="/loading">
            <button
              data-testid="login-submit-button"
              disabled={ isDisabled }
              className="button-login"
              type="button"
              onClick={ this.handleUserLog }
            >
              Entrar
            </button>
          </Link>
        </form>

      </div>
    );
  }
}
