import React, { Component } from 'react';
import { addSong, getFavoriteSongs } from '../../services/favoriteSongsAPI';
import Loading from '../../Pages/Loanding';

export default class MusicCard extends Component {
  constructor(props) {
    super(props);
    this.state = {
      isLoading: false,
      checkSaveds: [],
    };
  }

  handleSaveFavorites = async () => {
    this.setState({ isLoading: true });
    const { trackInfo } = this.props;
    await addSong(trackInfo);
    this.setState({ isLoading: false, checkSaveds: await getFavoriteSongs() });
  }

  render() {
    const { trackInfo: {
      previewUrl,
      key,
      trackName,
      trackId,
    } } = this.props;
    const { isLoading, checkSaveds } = this.state;

    return (
      <div key={ key }>
        <span>{trackName}</span>
        <audio data-testid="audio-component" src={ previewUrl } controls>
          <track kind="captions" />
        </audio>
        <label htmlFor="favorites" className="checkbox">
          {isLoading ? <Loading />
            : (
              <input
                name="favorites"
                data-testid={ `checkbox-music-${trackId}` }
                type="checkbox"
                checked={ checkSaveds.some((track) => track.trackId === trackId) }
                onChange={ this.handleSaveFavorites }
              />
            )}
          Favorita
        </label>
      </div>

    );
  }
}
