import React, { Component } from 'react';
import './CssPages/Album.css';
import Header from '../Components/Header';
import MusicCard from '../Components/MusicCard';
import getMusics from '../services/musicsAPI';

export default class Album extends Component {
  constructor(props) {
    super(props);
    this.state = {
      trackMusics: [],
    };
  }

  componentDidMount() {
    this.handleAlbumTracks();
  }

  handleAlbumTracks = async () => {
    const { params } = this.props.match;
    const trackMusics = await getMusics(params.id);
    this.setState({ trackMusics });
  }

  render() {
    const { trackMusics } = this.state;
    console.log(trackMusics);

    return (
      <div className="album" data-testid="page-album">
        <Header className="header-album" />
        <section className="album-section">
          <img src={ trackMusics[0].artworkUrl60 } alt="Foto Capa do Album" />
          <h1 data-testid="artist-name">{}</h1>
          <h2 data-testid="album-name">{}</h2>
          <div>
            {trackMusics
              .map((track) => <MusicCard key={ track.id } trackInfo={ track } />)}
          </div>
        </section>
      </div>
    );
  }
}
