import React, { Component } from 'react';
import './CssPages/Login.css';
import { createUser } from '../services/userAPI';

export default class Login extends Component {
  constructor(props) {
    super(props);
    this.state = {
      userForm: {
        name: '',
        email: '',
        image: '',
        description: '',
      },
      isDisabled: true,
    };
  }

  handleForm = ({ target: { name, value } }) => {
    this.setState({ [name]: value }, this.validationForm(name, value));
  }

  validationForm = (name, value) => {
    console.log(value, name);
    if (name === 'name') {
      this.setState({ isDisabled: value.length < 2 });
    }
  }

  render() {
    const { name, isDisabled, userForm } = this.state;
    return (
      <div data-testid="page-login" className="login">

        <form className="form">

          <label htmlFor="name">
            <input
              name="name"
              data-testid="login-name-input"
              type="text"
              className="name-login"
              onChange={ this.handleForm }
            />
            nome
          </label>

          <label htmlFor="email">
            <input
              name="email"
              data-testid="login-email-input"
              type="email"
              className="email-login"
              onChange={ this.handleForm }
            />
            email
          </label>

          <label htmlFor="image">
            <input name="image" type="file" />
            <img
              alt={ `Foto do perfil de ${name}` }
              data-testid="login-image-input"
              className="image-login"
              onChange={ this.handleForm }
            />
            Imagem
          </label>

          <label htmlFor="description">
            <textarea
              name="description"
              data-testid="login-description-input"
              className="description-login"
              onChange={ this.handleForm }
            />
            descrição
          </label>

          <button
            data-testid="login-submit-button"
            disabled={ isDisabled }
            className="button-login"
            type="button"
            onClick={ () => createUser(userForm) }
          >
            Entrar
          </button>
        </form>

      </div>
    );
  }
}
