import React, { Component } from 'react';
import PropTypes from 'prop-types';
import Header from '../Components/Header';
import Loading from './Loading';
import './CssPages/ProfileEdit.css';
import { getUser, updateUser } from '../services/userAPI';
import Logotunes from '../images/logo-trybetunes_black.png';
import imgDefault from '../images/image-alt.svg';

export default class ProfileEdit extends Component {
  constructor(props) {
    super(props);
    this.state = {
      userData: {},
      isLoading: true,
      isDisabled: true,
    };
  }

  componentDidMount() {
    this.setUserData();
  }

  setUserData = async () => {
    this.setState({ userData: await getUser() }, this.validationFillForm);
    this.setState({ isLoading: false });
  }

  handleFillForm = ({ target: { name, value, files } }) => {
    this.setState(({ userData }) => ({
      userData: {
        ...userData,
        [name]: value,
      },
    }), this.validationFillForm);
  }

  validationFillForm = () => {
    this.setState(({ userData }) => ({
      isDisabled: !Object.entries(userData)
        .filter((_valueKey, i) => i !== 2)
        .every((each) => each[1]),
    }));
  }

  handleClickSubmitUser = async () => {
    this.setState({ isLoading: true });
    const { userData } = this.state;
    await updateUser(userData);
    console.log(userData);

    this.handleToRedirect();
  }

  handleToRedirect = () => {
    const { history } = this.props;
    history.push('/profile');
  }

  render() {
    const { isLoading, isDisabled,
      userData: { name, email, image, description } } = this.state;
    return (
      <div className="edit">
        <Header className="header-edit" />
        {isLoading ? <Loading className="edit-loading" />
          : (
            <div className="form-container" data-testid="page-profile-edit">

              <section className="form-section">

                <span className="logoTunes">
                  <img
                    alt="Logo trybetunes"
                    src={ Logotunes }
                    width="100px"
                  />
                </span>

                <form className="form">

                  <input
                    name="name"
                    value={ name }
                    placeholder="Ajuste seu Nome"
                    data-testid="edit-input-name"
                    type="text"
                    className="name-edit"
                    onChange={ this.handleFillForm }
                  />

                  <input
                    name="email"
                    value={ email }
                    placeholder="Digite seu novo email"
                    data-testid="edit-input-email"
                    type="email"
                    className="email-edit"
                    onChange={ this.handleFillForm }
                    required
                  />

                  <input
                    name="image"
                    data-testid="edit-input-image"
                    type="image"
                    src={ image }
                    onChange={ this.handleFillForm }
                  />

                  <textarea
                    name="description"
                    value={ description }
                    placeholder="Ajuste seu perfil"
                    data-testid="edit-input-description"
                    className="description-edit"
                    maxLength="300"
                    onChange={ this.handleFillForm }
                  />

                  <button
                    data-testid="edit-button-save"
                    disabled={ isDisabled }
                    className="button-login"
                    type="button"
                    onClick={ this.handleClickSubmitUser }
                  >
                    Editar perfil
                  </button>
                </form>
              </section>
            </div>)}
      </div>
    );
  }
}

ProfileEdit.propTypes = {
  history: PropTypes.shape().isRequired,
};
