import React, { Component } from 'react';
import './style.css';
import { Link } from 'react-router-dom';
import logoTunes from '../../images/logo-trybeTunes_white.png';
import { getUser } from '../../services/userAPI';

export default class Header extends Component {
  constructor(props) {
    super(props);
    this.state = {
      userName: 'usuário',
    };
  }

  componentDidMount() {
    this.setState({ userName: getUser().name });
  }

  render() {
    const { userName } = this.state;
    return (
      <div className="header-container">
        <header data-testid="header-component">
          <span>
            <Link to="/">
              <img src={ logoTunes } alt="Logo da TrybeTunes" />
            </Link>
          </span>
          <div>
            <span data-testid="header-user-name">{userName}</span>
          </div>
        </header>
        <nav>
          <h2><Link to="/search">Pesquisa</Link></h2>
          <h2><Link to="/favorites">Favoritas</Link></h2>
          <h2><Link to="/profile">Perfil</Link></h2>
        </nav>
      </div>
    );
  }
}
